function clickGoBtn(mode, noVol, noIssue, pageInput) {
	if (mode == "1") {
		$("#pageMessage01").css("display", "none");
		$("#pageMessage02").css("display", "none");
		$("#pageMessage03").css("display", "none");
	} else if (mode == "2") {
		$("#mobilePageMessage01").css("display", "none");
		$("#mobilePageMessage02").css("display", "none");
		$("#mobilePageMessage03").css("display", "none");
	}
	try {

		if ((noVol == null || noVol == "") && (noIssue == null || noIssue == "")){
							noVol = "54";
				noIssue = "12"
					}

		if (noIssue == null || noIssue == ""){
			noIssue = "0"
		}

		if (pageInput == null || pageInput == "") {
			var url = getUrl(noVol, noIssue);
			url = url.replace('repXXX', noVol);
			url = url.replace('repYYY', noIssue);
			document.location.href = url;
			return;
		}
		if (noVol == null) {
			if (mode == "1") {
				$("#pageMessage01").css("display", "block");
			} else if (mode == "2") {
				$("#mobilePageMessage01").css("display", "block");
			}
			return;
		}
		var jsonUrl = 'https://www.jstage.jst.go.jp/pageSearch/jcej/repXXX/repYYY/repZZZ';
		jsonUrl = jsonUrl.replace('repXXX', noVol);
		jsonUrl = jsonUrl.replace('repYYY', noIssue);
		jsonUrl = jsonUrl.replace('repZZZ', pageInput);
		$.ajax({
			cache: false,
			type: 'GET',
			url: jsonUrl,
			dataType: 'json',
			contentType:'application/json',
			success: function(json) {
				var errorCd = json.errorCd;
				var redirectSryCd = json.redirectSryCd;
				var redirectNoVol = json.redirectNoVol;
				var redirectNoIssue = json.redirectNoIssue;
				var redirectCdArticle = json.redirectCdArticle;
				if (
						"0" == errorCd
						&& redirectSryCd != null && redirectSryCd != ''
						&& redirectNoVol != null && redirectNoVol != ''
						&& redirectNoIssue != null && redirectNoIssue != ''
						&& redirectCdArticle != null && redirectCdArticle != ''
				) {
					var shosiUrl = 'https://www.jstage.jst.go.jp/article/jcej/repXXX/repYYY/repZZZ/_article/-char/en';
					var urls = shosiUrl.match('(http.+/article/)([0-9a-z]+)(/repXXX.+)');
					if(urls.length == 4){
						shosiUrl = urls[1];
						shosiUrl += redirectSryCd;
						shosiUrl += urls[3];
					}
					shosiUrl = shosiUrl.replace('repXXX', redirectNoVol);
					shosiUrl = shosiUrl.replace('repYYY', redirectNoIssue);
					shosiUrl = shosiUrl.replace('repZZZ', redirectCdArticle);
					document.location.href = shosiUrl;
					return true;
				} else if ("1" == errorCd) {
					if (mode == "1") {
						$("#pageMessage02").css("display", "block");
					} else if (mode == "2") {
						$("#mobilePageMessage02").css("display", "block");
					}
				} else if ("2" == errorCd) {
					if (mode == "1") {
						$("#pageMessage03").css("display", "block");
					} else if (mode == "2") {
						$("#mobilePageMessage03").css("display", "block");
					}
				}
			}
		});
	} catch (e) {
	}
	return false;
}
function getUrl(noVol, noIssue) {
	return 'https://www.jstage.jst.go.jp/browse/jcej/repXXX/repYYY/_contents/-char/en';
}

