function doChangeLang(langKind){

	if (typeof changeLang == "function")  {
				changeLang(langKind);
		return;

	} else {
						var encStr = "";
		if(langKind == "ja") {
			encStr = "https%3A%2F%2Fwww.jstage.jst.go.jp%2Farticle%2Fjcej%2F53%2F9%2F53_19we078%2F_article%2F-char%2Fja";
		} else {
			encStr = "https%3A%2F%2Fwww.jstage.jst.go.jp%2Farticle%2Fjcej%2F53%2F9%2F53_19we078%2F_article%2F-char%2Fen";
		}
				location.href = decodeURIComponent(encStr);
				return;
	}
}
function hBack(){
		var ans;
		var ref = document.referrer;
		re = new RegExp(location.hostname,"i");
		if(ref.match(re)){
				ans = false;
		}else{
				ans = true
		}
		if(!ans){
			history.back();
		} else {
			location.href = "https://www.jstage.jst.go.jp/browse/-char/en";
		}
}
