}
}
},
CommonHTML: {
mtextFontInherit: true,
linebreaks: {
automatic: true
}
},
displayAlign: "center",
"HTML-CSS": {
linebreaks: {
automatic: true
},
availableFonts: ["Gyre-Pagella"],
preferredFont: "Gyre-Pagella",
webFont: "Gyre-Pagella",
mtextFontInherit: true,
scale: 85,
noReflows: false
},
SVG: {
linebreaks: {
automatic: true
} ,
font: "Gyre-Pagella",
mtextFontInherit: true
},
