div.script:
    function checkMail(){
	var f = document.mailform;

	if(!f.a_sender_email.value){
		f.a_sender_email.focus();
		f.a_ck_alert.value = "Please Your E-mail";
		return(false);
	}
	if (!f.a_recipient_email.value)	{
		f.a_recipient_email.focus();
		f.a_ck_alert.value = "Please Recipient's E-mail";
		return(false);
	}

	var regExp = /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
	if(!regExp.test(f.a_sender_email.value)){
		f.a_sender_email.focus();
		f.a_ck_alert.value = "Please Your E-mail";
		return(false);
	}

	if(!regExp.test(f.a_recipient_email.value)){
		f.a_recipient_email.focus();
		f.a_ck_alert.value = "Please Recipient's E-mail";
		return(false);
	}

	return(true);
}

function word_Counter(field, countfield, maxlimit) {
	if(field.value.length > 500){
			field.value = field.value.substring(0, 500);
			return;
		}

	countfield.value = field.value.length; 
}

