             setCookie("6FAC61F", "", 0);
         }
     });

     //아이디 저장 체크시 툴팁 숨김 처리
     $("#dev_pub_check_01").change(function () {
         if ($("#dev_pub_check_01").is(":checked")) {
             $("#dev_pub_tooltip_autoLogin").hide();
         }
     });

