         if (e.keyCode == 13) {
             loginProc();
         }
     });


     commonLoginFn().inputKeypress($('#pub_modalLoginPop .login_text.id'));
     commonLoginFn().inputKeypress($('#pub_modalLoginPop .login_text.pw'));
 });

 //b2c login popup 닫기
 function b2cClose() {
     commonLoginFn().hidePw();
     setCookie("b2cLayer", "N", 1);
     setCookie("location", "", 0);
 }

 function loginProc() {
     let userId = $("#layer_userID").val();
     let userPw = $("#layer_userPW").val();
     let loc = ifnull(getCookie("location"));
     let locCh = true;


     commonLoginFn().getHrefUrl().forEach(function (hrefUrl) {
         if (loc.indexOf(hrefUrl) !== -1) {
             locCh = false;
             return false;
         }
     });

     if (locCh) {
         loc = "";
     }

     var emsg = "아이디와 비밀번호를 모두 입력하세요.";
     if (userId == "" || userId == null || userPw == "" || userPw == null) {
         $.jQueryAlert(emsg);
         return false;
     } else {
         var autoLogin = $("#dev_pub_autoLogin").is(":checked") == true ? "Y" : "N";
         var data = {};
         data["userId"] = userId;
         data["userPw"] = userPw;
         data["autoLogin"] = autoLogin;
         if ($("#dev_pub_check_01").is(":checked")) {
             setCookie("idSave", $("#layer_userID").val(), 365);
         } else {
             setCookie("idSave", "", 0);
         }
         setCookie("refer", location.href, 1);
         $.ajax({
             url: ProxyUrl + "/member/b2cLoginProcForChrome",
             dataType: 'json',
             data: data,
             type: 'POST',
             success: function (result) {
                 if (result.loginYN == "S") {
                     $.fn.closeLayer('#pub_modalLoginPop');
                     if (loc == "" || loc == null) {
                         window.location.reload();
                     } else {
                         loc = fnUrlEncode(loc);
                         window.location = loc;
                     }

                 } else if (result.loginYN == "C") {
                     $.fn.closeLayer('#pub_modalLoginPop');
                     $.fn.openLayer("#pub_modalChangePwRegular2");
                 } else if (result.loginYN == "R") {
                     var url = location.hostname.indexOf("dbpia.co.kr") > -1 ? "https://" : "http://";
                     url += location.hostname + "/member/dormancyDis";
                     jQuery.jQueryAlertMove(result.loginMsg, url);
                 } else {
                     //로그인 실패
                     if (result.loginMsg.trim() != "") {
                         emsg = result.loginMsg;
                     }
                     $.jQueryAlert(emsg);
                 }
             }
         });
     }
 }


