   let data = {};
    $(".scrollList").css("display", "none");

    let msg = "";
    let dev_search_idx = 0;
    let dev_search_sum = 0;
    let curPlace = "#b2b_login_pop_basic";
    let scrollList = $(curPlace + " .scrollList");
    let libAuthStaCode;

    if (msg == "서비스가 중지된 논문입니다.") {
        $.jQueryAlertMove(msg, '/');
    } else if (msg !== '') {

        if ('' === 'Y') $.jQueryAlertMove(msg, '/notice/noticeView?nId=1137&topYn=N');
