    function Pop_none() {
        $('#organLoginSearchPop').parent('.modalWrap').hide();
    }

    $("#dev_search").keydown(function (e) {
        $('#dev_search').css('color', '#111111');
        let keyCode = e.keyCode;
        let organPopNm = $('#organLoginSearchPop').parent('.modalWrap');


        if (keyCode === 13) {
            if (organPopNm.css('display') !== 'none') {
                Pop_none();
                return;
            }
            let keyword = $("#dev_search").val();
            if (ifnull(keyword).trim().length < 2) {
                organPopNm.show();
                return;
            }
            let scrollListSelect = scrollList.find('.select_li');
            if (scrollListSelect.length > 0) {
                selectItem(scrollListSelect.children('a').attr('id'), scrollListSelect.text());
            } else {
                dev_search_idx = 1;
                dev_search_sum = commonLoginFn().getB2bListAjax(keyword, curPlace);
                commonLoginFn().searchScrollTopFn(3, dev_search_idx, scrollList);
                fn_statistics('T506', 'ICST00001411', '');
            }
        } else if (dev_search_sum > 0 && (keyCode === 38 || keyCode === 40)) {

            if (keyCode === 38) {
                dev_search_idx--;
                if (dev_search_idx <= 0) {
                    dev_search_idx = dev_search_sum;
                    commonLoginFn().searchScrollTopFn(2, dev_search_idx, scrollList);
                } else {
                    commonLoginFn().searchScrollTopFn(1, dev_search_idx, scrollList);
                }

            } else if (keyCode === 40) {
                dev_search_idx++;
                if (dev_search_idx > dev_search_sum) {
                    dev_search_idx = 1;
                    commonLoginFn().searchScrollTopFn(4, dev_search_idx, scrollList);
                } else {
                    commonLoginFn().searchScrollTopFn(3, dev_search_idx, scrollList);
                }
            }
            e.preventDefault();
        } else {
            scrollList.find('.select_li').removeClass('select_li');
        }
    })

    $("#dev_searchBtn").click(function () {
        let keyword = $("#dev_search").val();
        if (ifnull(keyword).trim().length < 2) {
            $('#organLoginSearchPop').parent('.modalWrap').show();
            return;
        }
        dev_search_idx = 1;
        dev_search_sum = commonLoginFn().getB2bListAjax(keyword, curPlace);
        commonLoginFn().searchScrollTopFn(3, dev_search_idx, scrollList);
        $("#dev_search").focus();
        fn_statistics('T506', 'ICST00001411', '');
    });


