    function b2bLoginFn(loginTypePath) {
        loginTypePath = "#" + loginTypePath;
        if ($(loginTypePath + " input[name='b2bAcc']").val() === "") {
            $.jQueryAlert("아이디를 입력하세요.");
            return false;
        }
        if ($(loginTypePath + " input[name='userPass']").val() === "") {
            $.jQueryAlert("비밀번호를 입력해 주세요.");
            return false;
        }
        if ($(loginTypePath + " input[name='b2bName']").val() === "") {
            $.jQueryAlert("소속기관 이름을 입력해 주세요.");
            return false;
        }
        if ($(loginTypePath + " input[name='b2bId']").val() === "") {
            $.jQueryAlert("소속기관을 선택하세요.");
            return false;
        }

        setCookie("refer", location.href, 1);

        $.ajax({
            type: "POST",
            url: ProxyUrl + "/member/b2bLoginProc",
            data: $(loginTypePath).serialize(),
            success: function (data) {
                let loginSucYN = data.loginSucYN;
                if (loginSucYN === 'N') {
                    let b2bLoginType = data.b2bLoginType;
                    switch (b2bLoginType) {
                        case '151003' :
                            $.jQueryAlert("소속 기관과 아이디 또는 비밀번호를 확인해 주세요!
도서관 홈페이지를 한번이라도 이용한 경험이 있어야합니다"); //로그인 실패
                            break;
                        case '151004' :
                            $.jQueryAlert("소속 기관과 아이디 또는 비밀번호를 확인해 주세요!"); //로그인 실패
                            break;
                        default :
                            $.jQueryAlert("에러가 발생하였습니다
캐시를 지우고 다시 시도해보세요"); //에러 발생
                            break;
                    }
                } else if (loginSucYN === 'Y') {
                    let loginMsg = data.loginMsg;
                    let redirectPage = data.redirectPage;
                    $.jQueryAlertMove(loginMsg, redirectPage);
                }
            },
            error: function (e) {
                $.jQueryAlert("에러가 발생하였습니다
캐시를 지우고 다시 시도해보세요"); //에러 발생
            }
        });
    }

    function selectItem(b2bId, innerText) {
        //선택시 권한 체크
        let obj = getB2bPermission(b2bId);

        if (obj.auth !== "0") { //구독기관
            reSearchHide(innerText);
            $('.ok_organ_container #organ_Nameo1').html(innerText);
            $('.ok_organ_container').show();

            if (obj.b2b_localYN === "Y" || obj.b2b_bannerYN === "Y") {//자관, 배너
                $('.ok_organ_container .ok_organ_oneon .firs_padd').show();
                if (obj.b2b_localYN === "Y") { //자관
                    libAuthStaCode = "T512";
                    $('#library_pop_auth #local_pop_auth_sec').show();
                    $('#library_pop_auth #local_pop_auth_que').show();
                } else if (obj.b2b_bannerYN === "Y") { //배너
                    libAuthStaCode = "T510";
                    $('#library_pop_auth #banner_pop_auth_sec').show();
                    $('#library_pop_auth #banner_pop_auth_que').show();
                    if (ifnull(obj.library_addr) !== '') { //도서관 주소 하이퍼링크
                        let checkLibraryNull = obj.library_addr; //도서관 주소가 정상적인지 체크
                        checkLibraryNull = checkLibraryNull.replace("http", "").replace("https", "");
                        //http, https 문자열을 제외하고 4글자 이상이면 정상이라고 판단(:// 등의 문자만 있을 수도 있기 때문)
                        if (checkLibraryNull.length >= 4) {
                            $('#goToLibraryLogin').show();
                            $('#goToLibraryLogin').attr("href", obj.library_addr);
                        }
                    }
                }
            }
            if (obj.b2b_memberYN === "Y") {//계정
                $('.ok_organ_container .ok_organ_oneon .seco_padd2').show();
                if (ifnull(obj.b2b_tel) !== "") {
                    $('#member_pop_auth .consult_number').html(""+innerText+" 담당자 :" +
                            " " + obj.b2b_tel);
                    $('#member_pop_auth .consult_number').show();
                } else {
                    $('#member_pop_auth .consult_number').hide();
                }
            }
            if (obj.b2b_ipYN === "Y") {//IP인증
                $('.ok_organ_container .ok_organ_oneon .thre_padd2').show();
            }

            if (obj.auth === "4") { //IP 인증만
                $(curPlace + ' .subject_inquiry_container').show();
            }
        } else { //미구독 기관
            reSearchHide(innerText);
            $('.no_organ_container #organ_Nameo2').html(innerText);
            $('.no_organ_container').show();
        }

        $("#dev_search").val(innerText);
        $(".dev_b2bId").val(b2bId);
        $('#dev_search').css('color', '#3c63e0');
        $('.scrollList').hide();
    }

    function reSearchHide(innerText) {
        $(curPlace + ' .explanation').hide();
        $('.ok_organ_container').hide();
        $('.check_organ_box').hide();
        $('.scroll_list_comt').hide();
        $('.ok_organ_container .ok_organ_oneon .firs_padd').hide();
        $('.ok_organ_container .ok_organ_oneon .seco_padd2').hide();
        $('.ok_organ_container .ok_organ_oneon .thre_padd2').hide();
        $('.no_organ_container').hide();


        $('#library_pop_auth #local_pop_auth_sec').hide();
        $('#library_pop_auth #local_pop_auth_que').hide();
        $('#library_pop_auth #banner_pop_auth_sec').hide();
        $('#library_pop_auth #banner_pop_auth_que').hide();
        $('#goToLibraryLogin').hide();


        $(curPlace + ' .subject_inquiry_container').hide();


        if (ifnull(innerText) !== '') {
            if (innerText.length >= 20) {
                $('.tit_br1').css('display', 'block');
                $('.tit_br2').css('display', 'block');
                $('.no_organ_container_tit').css('text-align', 'center')
            } else {
                $('.tit_br1').css('display', 'none');
                $('.tit_br2').css('display', 'none');
            }
        }
    }

