    $('.subject_contentbox').click(function () {
        let descbox = $(this).children('.subject_desc');
        let titimg = $(this).children('.subject_tit').children('.slide_img');
        let remaindbox = $(this).siblings('.subject_contentbox').children('.subject_desc');
        let remaindimg = $(this).siblings('.subject_contentbox').children('.subject_tit').children('.slide_img');
