            descbox.slideDown('200');
            titimg.attr("src", "/images/common/question_up.png");

            remaindbox.slideUp('200');
            remaindimg.attr("src", "/images/common/question_down.png");
        } else {

            descbox.slideUp('200');
            titimg.attr("src", "/images/common/question_down.png");
        }
    });



