    /* 추가된 함수  */
    function resizeTooltip() {
        $(".popup_tooltip.tooltip_news").removeClass('wide');
    }

    $(window).on('load', function () {
        $('.popup_tooltip.tooltip_news').hover(function () { //mouseenter에 해당하는 이벤트핸들러
            $(this).addClass('wide');
        }, function () { //mouseleave에 해당하는 이벤트 핸들러
            $(this).removeClass('wide');
        });

        $("#btnTop").click(function () {
            $('html, body').animate({
                scrollTop: 0
            }, 200);
            return false;
        });
        var windowWidth = $(this).width();

        $(window).scroll(function () {
            if (windowWidth < 959) {
                if ($(this).scrollTop() > 500) {
                    $('.gTopNavi').fadeIn(50);
                } else {
                    $('.gTopNavi').fadeOut(50);
                }
            }

        });

    });

    function getRandomInt(min, max) {
        min = Math.ceil(min);
        max = Math.floor(max);
        return Math.floor(Math.random() * (max - min)) + min; //최대값은 제외, 최소값은 포함
    }


    var last_quote_type = "";
    var cttn_div_code = "";

    var titleKR = "Design Optimization of High-Speed Machines for Underwater Electric Thrusting Systems considering Magnetic Flux Density Distribution";
    var titleEN = "";
    var titleEQ = "";
