    function popupTooltipShow() {
        var tooltipFirst = $("[data-tooltip='tooltip_first']");
        if (tooltipFirst.length > 0) {
            var tooltipPos = tooltipFirst.offset();
            var tooltipId = tooltipFirst.data("tooltip");
            tooltipPos.left = -65;
            tooltipPos.bottom = -46;
            tooltipPos.top = "auto";
            var getToolTip = $(".popup_tooltip." + tooltipId);
            getToolTip.appendTo(tooltipFirst.parent().css("position", "relative"));
            getToolTip.css(tooltipPos).fadeIn(200);
        }
    }

    function popupTooltipNewShow() { /* 위에 있는 tooltip_new 를 보여줌 */
        if (typeof (getCookie('eventTooltip')) == 'undefined' || getCookie('eventTooltip') == null || getCookie('eventTooltip') == '') {
            var animateSlide = 1500;
            var tooltipNew = $(".popup_tooltip.tooltip_news");
            if (tooltipNew.length > 0) {
                tooltipNew.show().animate({right: 115, opacity: 1}, animateSlide);
                setTimeout("resizeTooltip()", 6000);
            }

            $(".popup_tooltip .btn_close").on("click", function () {
                $(this).closest(".popup_tooltip").stop(true, true).fadeOut(200);
                return false;
            });

            //플로팅 배너 오늘 하루 안보기: 당일 23:59:59까지만 등장하지 않음.
            $(".popup_tooltip .btn_close_for_today").on("click", function () {
                var date = new Date();
                var maxTime = new Date(date.setHours(23, 59, 59, 0));
                document.cookie = escape("eventTooltip") + "=" + escape("done") + "; path=/; expires=" + maxTime.toUTCString();
                $(this).closest(".popup_tooltip").stop(true, true).fadeOut(200);
                return false;
            });
        }
    }

    $(document).ready(function () {

        $(document).on('mouseenter', '.btn_close_for_today', function () {
            $(this).css("text-decoration", "underline");
        });

        $(document).on('mouseleave', '.btn_close_for_today', function () {
            $(this).css("text-decoration", "");
        });

        $(document).on('mouseenter', 'a[name="viewList"]', function () {
            $(this).css("text-decoration", "underline");
        });

        $(document).on('mouseleave', 'a[name="viewList"]', function () {
            $(this).css("text-decoration", "");
        });

        var filter = "win16|win32|win64|mac|macintel";
        //20210727  모바일 추가
        if (filter.indexOf(navigator.platform.toLowerCase()) < 0) {
            $("#floatIm").attr("src", "https://nurimedia-upload.s3.ap-northeast-2.amazonaws.com/service/popup/popMobile.jpg?ver=20220110-01");
        }

        // 비로그인시 북마크 유지
        if (getCookie("bookmark") != null) {
            var bookMarkNode = getCookie("bookmark").split('\|');
            ;

            var checkBookmark = bookMarkNode.indexOf("NODE07239686");

            if (checkBookmark > -1) {
                $("#toggleBook").addClass("selected on");
                $("#toggleBook2").addClass("selected on");
                $("#toggleBook3").addClass("selected on");
                $("span[name='bookmark']").text("");
                $("span[name='bookmark']").text("내서재 담김");
            }
        }

        $(document).on('click', 'button[name="bookcheck"]', function () {
            var elem = $(this).attr("class");

            if (elem == 'btn btnIconB darkGrayColor xl btnLine bookmarkSelected bookmark selected on') {
                $("span[name='bookmark']").text("");
                $("span[name='bookmark']").text("내서재 담김");
            } else {
                $("span[name='bookmark']").text("");
                $("span[name='bookmark']").text("내서재 담기");
            }
        });

        // 2021-05-24 최근본 목록 쿠키 관련
        if (dev_b2cId_chk != "") {

            var nodes = '';

            if (getCookie("readNode") != null) {
                var readNode = getCookie("readNode").split('\|');
                // 쿠키에 들어있는 노드 아이디 가져와서 배열에 담기
                nodes = readNode.join();
            }

            $.ajax({
                type: "POST"
                , url: ProxyUrl + '/journal/insertReadNode'
                , data: {"nodes": nodes}
                , dataType: "JSON"
                , success: function (data) {

                    $("#viewL1").empty();
                    $("#viewL2").empty();

                    var viewList1 = "";
                    var viewList2 = "";

                    if (data.list.length < 6) {
                        $('#viewBtn').empty();
                    }

                    $.each(data.list, function (idx, val) {

                        if (idx < 5) {
                            viewList1 += '' + val.NODE_SVC_TTLE + '';
                        }

                        if (idx > 4 && idx < 10) {
                            viewList2 += '' + val.NODE_SVC_TTLE + '';
                        }
                    });

                    $('#viewL1').append(viewList1);
                    $('#viewL2').append(viewList2);

                }, error: function (e) {
                    console.log(e);
                }

            });
            setReadNodeCookies("readNode", "", -1);
        } else {

            var nodes = '';

            if (getCookie("readNode") != null) {
                var readNode = getCookie("readNode").split('\|');
                // 쿠키에 들어있는 노드 아이디 가져와서 배열에 담기
                nodes = readNode.join();

                $.ajax({
                    type: "POST"
                    , url: ProxyUrl + '/journal/readNode'
                    , data: {"nodes": nodes}
                    , dataType: "JSON"
                    , success: function (data) {

                        $("#viewL1").empty();
                        $("#viewL2").empty();

                        var viewList1 = "";
                        var viewList2 = "";

                        if (data.list.length < 6) {
                            $('#viewBtn').empty();
                        }

                        $.each(data.list, function (idx, val) {

                            if (idx < 5) {
                                viewList1 += '' + val.NODE_SVC_TTLE + '';
                            }

                            if (idx > 4 && idx < 10) {
