    $(document).on({
        mouseenter: function () {
            var pos = $(this).offset();
            $("ntooltip").stop(true, true).remove();
            var ntoolTip = $('
' + $(this).data("tooltip") + '');
            $("body").append(ntoolTip);
            ntoolTip.css({
                left: pos.left - ntoolTip.width(),
                top: pos.top - ntoolTip.height()
            }).stop(true, true).fadeIn(100);
        }, mouseleave: function () {
            $(".ntooltip").fadeOut(100, function () {
                $(this).remove();
            });
        }
    }, '.ntooltip_box');

    // 원문복사 신청
    function showBanner() {	//원문 저장 신청
        var nodeId = "NODE07239686";
        var btnType = "1";
        var b2bId = "ICST00001411";

        if (btnType == "2" && b2bId != "") {
            $.ajax({
                type: "POST"
                , url: ProxyUrl + '/journal/showBanner'
                , dataType: "JSON"
                , success: function (data) {
                    if (data != null && Object.keys(data).length && data.NODECOPY_YN == "Y") {
                        if (b2bId === 'ICST00000655') {
                            data.LIBY_HMPG_ADRS = 'https://library.scnu.ac.kr/#/guide/illdds/guide'
                        }
                        $("#pub_notification").show();
                        $("#pub_notification").find("a").attr("onclick", "fnArticleCopyRequest('NODE07239686', '" + data.LIBY_HMPG_ADRS + "')");
                    }
                }
                , error: function (e) {
                    console.log(e);
                }
            });
        }
    }

