    //관리자 인용양식 불러오기(제공 양식 불러오기 클릭 시)
    function fnAdminQuoteList() {
        var last_quote_type = "";
        var last_quote = "";
        var quote_type = $("#dev_btn_quoteType").children('span').text();
        $(".tooltip .desc.leftTop").text('내가 등록해놓은 양식을 불러옵니다. 마이페이지 > 인용양식 설정에서 내가 원하는 양식을 등록해보세요!');

        $.ajax({
            type: "POST",
            url: ProxyUrl + "/mylib/searchAdminQuote",
            success: function (data) {
                if (Array.isArray(data) == true) {
                    $("#dev_quoteList").empty();
                    if (data.length == 0) {
                        $("#dev_quoteList").append('
인용양식이 없습니다.');
                    }
                    $.each(data, function (idx, val) {
                        var quoteList = '';
                        if (val.cttnStleId == last_quote) { //최근 사용 양식이 관리자 양식일때 최근사용양식 selected
                            $("#dev_sel_quote").val(val.cttnStleId);
                            $("#dev_sel_quote_nm").val(val.ttle);
                            quoteList += '
';
                        } else {
                            quoteList += '
';
                        }
                        quoteList += '' + val.ttle + '';
                        $("#dev_quoteList").append(quoteList);
                    });
                } else {
                    $.fn.closeLayer("#pub_modalQuoteThesis");
                    $.fn.openLayer('#pub_modalLoginPop');
                }

            }
        });
        $("#dev_btn_quoteType").children('span').text("내 양식 불러오기");
        $("#dev_btn_quoteType").attr("onclick", "fnSearchUserQuote()");
    }

    //사용자 인용양식 불러오기
    function fnSearchUserQuote() {
        $("#quote_type").val("Y");
        var last_quote_type = "";
        var last_quote = "";
        $(".tooltip .desc.leftTop").text('DBpia가 기본 제공하는 양식을 불러옵니다.');
        $.ajax({
            type: "POST",
            url: ProxyUrl + "/mylib/searchUserQuote",
            success: function (data) {

                if (Array.isArray(data) == true) {
                    $("#dev_quoteList").empty();
                    if (data.length == 0) {
                        $("#dev_quoteList").append('
인용양식이 없습니다.');
                    }
                    var quoteList = "";
                    $.each(data, function (idx, val) {
                        var cttnDivCode = val.cttnDivCode;

                        if (val.cttnStleId === '') {
                            val.cttnStleId = '4';
                            val.ttle = 'APA 양식';
                            $("#dev_sel_quote").val(val.cttnStleId);
                            $("#dev_sel_quote_nm").val(val.ttle);
                        }
                        if (val.cttnStleId == last_quote) { //최근 사용 양식이 관리자 양식일때 최근사용양식 selected
                            $("#quote_type").val(cttnDivCode);
                            $("#dev_sel_quote").val(val.cttnStleId);
                            $("#dev_sel_quote_nm").val(val.ttle);
                            quoteList += '
';
                        } else {
                            $("#dev_sel_quote").val(val.cttnStleId);
                            $("#dev_sel_quote_nm").val(val.ttle);
                            quoteList += '
';
                        }
                        quoteList += '' + val.ttle + '';

                    });
                    quoteList += '
';
                    quoteList += '';
                    quoteList += '';
                    quoteList += '인용양식 추가';
                    quoteList += '';
                    quoteList += '';
                    $("#dev_quoteList").append(quoteList);
                    $("#selected").click();

                } else {
                    $.fn.closeLayer("#pub_modalQuoteThesis");
                    $.fn.openLayer('#pub_modalLoginPop');
                }

            }
        });
        $("#dev_btn_quoteType").children('span').text("기본 양식 불러오기");
        $("#dev_btn_quoteType").attr("onclick", "fnAdminQuoteList()");
    }

