    //인용양식 선택 했을 때
    function fnSelectQuote(cttnDivCode, sel, quote_lang) {

        $("#quote_type").val(cttnDivCode);
        $("#dev_sel_quote").val($(sel).find('input[name=dev_quote_id]').val()); //선택된 인용양식 번호 셋팅
        $("#dev_sel_quote_nm").val($(sel).find('span[name=dev_quote_nm]').text());
        $(".gSelectedOne").removeClass("selected");
        if (!$(sel).next().is(":visible")) {
            $(sel).addClass("selected");
        }
        var ttle = $(sel).find('span[name=dev_quote_nm]').text();
        var sttnStleId = $(sel).find('input[name=dev_quote_id]').val();
        var nodeId = "NODE07239686";

        //인용양식 팝업을 처음 열 경우, 언어 값이 세팅되어 있지 않아 세팅한다.
        if ($('#quote_lang').val() == '') {
            quote_lang = 'ko_KR';
            $('#quote_lang').val(quote_lang);

            //언어값이 세팅된 상태
        } else {
            quote_lang = $('#quote_lang').val();
        }

        $.ajax({
            type: "POST",
            url: ProxyUrl + "/mylib/getQuoteNodeList",
            data: {"node_id": nodeId, "cttnStleId": sttnStleId, "quote_lang": quote_lang, "quote_type": cttnDivCode},
            success: function (data) {


                $("#dev_selectCon").empty();

                var selectCon = '';
                selectCon += '
';

                switch (ttle) {
                    case 'APA 양식' :
                        selectCon += '
APA : 사회과학 및 일부 자연과학 분야 선호 스타일 [기본양식]';
                        break;
                    case 'MLA' :
                        selectCon += '
MLA : 인문학 분야 선호 스타일';
                        break;
                    case 'Chicago' :
                        selectCon += '
Chicago : 일반인문 및 자연과학 분야 선호 스타일';
                        break;
                    case 'ACS' :
                        selectCon += '
ACS : 자연과학 선호 스타일';
                        break;
                    case 'Harvard' :
                        selectCon += '
Harvard : 사회과학 분야 선호 스타일';
                        break;
                    case 'IEEE' :
                        selectCon += '
IEEE : 공학 분야 선호 스타일';
                        break;
                    default :
                        selectCon += '
' + ttle + '';
                }

                selectCon += ' 
' + data[0] + '';
                selectCon += '';
                selectCon += '
';
                selectCon += '
';
                selectCon += '';
                selectCon += '';
                selectCon += '한';
                selectCon += '';
                selectCon += '';
                selectCon += '';
                selectCon += '영';
                selectCon += ''; //김재경
                selectCon += '';
                selectCon += '
';
                selectCon += '인용양식 저장';
                selectCon += '';

                $("#dev_selectCon").append(selectCon);

                //선택된 언어에 맞게 체크 표시
                if (quote_lang == 'ko_KR') {
                    $('#language_radio_01').prop('checked', true);
                    $('#language_radio_02').prop('checked', false);

                } else {
                    $('#language_radio_02').prop('checked', true);
                    $('#language_radio_01').prop('checked', false);
                }

            }
        });
    }

