    //인용양식 언어 변환
    function selectLanguage(lang) {

        if (lang == 'ko_KR') {

            $('#quote_lang').val('ko_KR');

        } else if (lang == 'en_US') {

            $('#quote_lang').val('en_US');

        }

        //언어를 변환하면서, 인용양식도 변경되어야 하기때문에 클릭을 해준다.
        $('#dev_quoteList a.selected').click();
    }


    function saveQuote(cttnDivCode, lang) {

        var quote_type = ""; //인용양식 코드
        if (cttnDivCode == "414003" || cttnDivCode == "414002" || cttnDivCode == "") {
            quote_type = "N"; //APA 타입일때와 관리자 타입일 때 인용양식 타입 관리자로 셋팅
        } else {
            quote_type = "Y"; //사용자 인용양식일 때 인용양식 타입 사용자로 셋팅
        }

        var sttnStleId = $("#dev_sel_quote").val();

        $.ajax({
            type: "POST",
            url: ProxyUrl + "/mylib/setQuoteHistory",
            data: {"cttnStleId": sttnStleId, "quote_type": quote_type, "cttnDivCode": cttnDivCode, "quote_lang": lang},
            async: false,
            success: function (data) {
                $.fn.closeLayer("#pub_modalQuoteThesis2");
                window.location.reload();
            }
        });
    }

    //리뷰쓰기,수정 전송
    function updateSend(params) {
        params.nodeId = "NODE07239686";
        params.userId = "";

        var varLang = 'ko_KR';

        if (params.userId != "") {
            var msg = "처리 실패. 다시 시도해 주세요.";
            $.ajax({
                url: ProxyUrl + params.url,
                type: "POST",
                data: params,
                dataType: "json",
                success: function (data) {
                    if (data.success == -1) {
                        msg = "아이디 혹은 비밀번호를 다시 입력해 주세요.";
                    } else if (data.success == 0) {
                        if (params.url == "/journal/revwDelete") {
                            msg = "리뷰가 삭제되었습니다.";
                        } else if (params.url == '/journal/revwClaim') {
                            msg = "신고가 완료되었습니다.";
                        } else {
                            msg = "리뷰가 등록되었습니다.";
                        }
                        //내용 초기화
                        $('textarea').val('');
                        $("#radio_01").prop("checked", true);
                        reviewListCall();
                    }
                },
                error: function (e) {
                    console.log(e);
                },
                complete: function () {
                    $.jQueryAlert(msg, varLang);
                }
            });
        } else {
            fnLoginLayer();
        }
    }

    //리뷰 불러 오기
    function reviewListCall() {
        $.ajax({
            url: ProxyUrl + "/journal/revwList/",
            type: "POST",
            data: {nodeId: "NODE07239686"},
            dataType: "json",
            success: function (data) {
                if (data.reviewer != null) {
                    printReviewInput(data.reviewer);
                }
                //리뷰 사용여부 체크
                if (data.useReview == 1) {
                    $('#pub_review').show();
                    printReviewList(data);
                    $('#dev_revw_cnt_ank').show();
                } else {
                    $('#btn_collection_tooltip').css("right", "82px");
                    $('#btn_quote_menu').css("margin-right", "10px");
                }
            },
            error: function (e) {
                console.log(e);
            }
        });
    }

    //글자바이트 수 체크
    function revwCntChk(elem, end) {
        var cnt = calByte.getByteLength($(elem).val());
        if (cnt > end) {
            var txt = calByte.cutByteLength($(elem).val(), end);
            $(elem).val(txt);
            $.jQueryAlert(end + " byte까지만 작성하실 수 있습니다.");
            cnt = calByte.getByteLength($(elem).val());
        }
        return cnt;
    }

    //리뷰 쓰기 창 그리기
    function printReviewInput(info) {
        if (info != null) {
            $("div.reviewHead").find("span.dept").html(info.major);
            $("div.reviewHead").find("#masked_id").html(info.maskedId);
        }
        if (info.block > 0) {
            $("#dev_noAuth").show().css("position", "absolute");
            $("#dev_noReview").hide();
        } else {
            $("#dev_noAuth").hide();
            $("#dev_noReview").show();
        }
    }


    //리뷰 리스트 출력
    function printReviewList(info) {
        $("p.review span.count").html(commaChk(info.size));
        $("#dev_revw_cnt").html("(" + commaChk(info.size) + ")");//리뷰 수
        if (info.size > 0) {
            $("#dev_revw_cnt_ank").append('' + commaChk(info.size) + '');//리뷰 수
        }
        $("#dev_helpY_cnt").html(commaChk(info.help));//도움 수
        $("#dev_helpN_cnt").html(commaChk(info.nhelp));//안도움 수
        var $listArea = $(".reviewList ul.list");
        var content = '';
        var maxSize = 10;

        $listArea.empty();

        if (info.size > 0) {
            $("#dev_reviewInputBox").show();

            info.reviews.forEach(function (item, idx) {
                if (idx < maxSize) {
                    content += '
';
                } else {
                    content += '
';
                }
                content += '
';
                content += '
';

                //블록 될 경우 id 관리자료 표시
                var user_id = item.masked_id;
                if (item.block_status_code != null) {
                    user_id = '관리자';
                }
                content += '' + user_id + '';

                if (item.majr_nm != null) {
                    content += '' + item.majr_nm + '';
                }
                content += '';
                content += '
' + item.elpn.replace(/\n/g, '
');
                +'';
                content += '';
                //21.10.22, 리뷰 도움말 기능 삭제되어 주석 처리 된 것 지움, jaekyungkim
                content += '
' + item.updt_day.split(" ")[0] + '';
                //21.10.22, 리뷰 도움말 기능 삭제되어 주석 처리 된 것 지움, jaekyungkim
                content += '';

                content += '
';
                content += '';
                content += '';
                content += '
';
                if (item.rgst_user_id == dev_b2cId_chk) {

                    // 리뷰를 차단 된 경우에는 수정 불가 block = 1 차단
                    if (info.reviewer.block > 0) {
                        content += '';
                        content += '삭제';
                    } else {
                        content += '수정'
                                + '삭제';
                    }

                } else {
                    content += '신고';
                }
                content += '';

                if (item.rgst_user_id == dev_b2cId_chk) {
                    // 리뷰를 차단 된 경우에는 수정 불가 block = 1 차단
                    if (info.reviewer.block <= 0) {
                        content += printUpdateForm(item, idx);
                    }
                }
                content += '';
            });
        } else {
            content += '
';
            content += '
';
            content += '첫번째 댓글을 남겨주세요.';
            content += '';
            content += '';
        }

        $listArea.append(content);

        if (info.size > maxSize) {
            $("#dev_revw_more").empty();
            $("#dev_revw_more").append('리뷰 더보기');
        }

        if ($("#dev_revw_cnt_ank").text() === '(0)')
            popupTooltipShow();
    }

    function revwMore(max) {
        $("#dev_revw_more").empty();
        var $list = $(".reviewList li.item.hidden");
        var length = max < $list.size() ? max : $list.size();
        for (var i = 0; i < length; i++) {
            $list.eq(i).removeClass("hidden");
        }

        $list = $(".reviewList li.item.hidden");
        if ($list.size() > 0) {
            $("#dev_revw_more").append('리뷰 더보기');
        }

    }

