        content += '';

        if (elem.majr_nm != null) {
            content += '' + elem.majr_nm + '';
        }

        content += '';
        content += '
';
        content += '';
        content += '';
        content += '';
        content += elem.elpn + '';
        content += '' + calByte.getByteLength(elem.elpn);
        content += '/1000byte';
