        content += ''
        content += '저장';

        content += '';
        content += '';
        content += '';
        content += '';
        return content;
    }

    //리뷰 수정하기 폼
    function showUpdateForm(elem) {
        var $edit = $(elem).parentsUntil("ul.list").eq(1).find("#pub_editArticle");
        $edit.show();

        $edit.find("button").click(function () {
            if ($edit.find("input[type='text']").val().trim() == "") {
                $edit.find("input[type='text']").focus();
                $.jQueryAlert("작성자를 입력하세요.");
                return false;
            }
            if ($edit.find("textarea").val().trim() == "") {
                $edit.find("textarea").focus();
                $.jQueryAlert("리뷰 내용을 입력하세요.");
                return false;
            }

            var params = {};
            params.url = "/journal/revwUpdate/";
            params.reviewId = $edit.find("input[name='revwid']").val();
            params.content = $edit.find("textarea").val();
            params.help = $edit.find("input:checked").val();
            params.writer = $edit.find("input[type='text']").val();
            updateSend(params);

            reviewListCall();
            $edit.hide();
        });
    }

    //리뷰 삭제
    function delReview(no) {
        $.jQueryAlertTwoButton("리뷰를 삭제하시겠습니까?");
        $(".ui-dialog-buttonset button").on("click", function () {
            if ($(this).html() == "확인") {
                var params = {};
                params.url = "/journal/revwDelete";
                params.reviewId = no;
                updateSend(params);
            }
        });
    }

