    // 레이어 팝업 확인버튼 클릭
    $(document).on('click', 'button[name=btnDoneModal]', function () {
        var varLang = 'ko_KR';
        var varOpenPopLayer = $('#openPopLayer');
        var varPopSec = varOpenPopLayer.find('#popSection').val();

        if (varPopSec === 'reviewClaim') {	// 리뷰 신고하기 팝업 callback
            var varContent = varOpenPopLayer.find('textarea').val();
            var varReviewId = varOpenPopLayer.find('#reviewId').val();

            if (varContent === '') {
                $.jQueryAlert('신고내용을 입력하세요.', varLang);
            } else {

                $.fn.closeLayerNew('#openPopLayer');

                var params = {
                    url: '/journal/revwClaim',
                    reviewId: varReviewId,
                    content: varContent
                };

                updateSend(params);	// 신고 처리
            }

        } else if (varPopSec === 'errorReport') {		// 오류 제보하기 팝업 callback
            var url = "";
            var valimsg = "";
            var state = varOpenPopLayer.find("#pub_selectDataType").val();
            var param = {nodeId: $("#articleId").val()};	// 논문 아이디

            switch (state) {
                case "416001"://제목
                    if (varOpenPopLayer.find("#mainTtl").val().trim() == "" && varOpenPopLayer.find("#subTtl").val().trim() == "" && varOpenPopLayer.find("#eqTtl").val().trim() == "") {
                        valimsg = "수정하실 제목을 입력해 주세요.";
                    } else {
                        url = "/journal/errReport/title";
                        param.mainTtl = varOpenPopLayer.find("#mainTtl").val();
                        param.subTtl = varOpenPopLayer.find("#subTtl").val();
                        param.eqTtl = varOpenPopLayer.find("#eqTtl").val();
                    }
                    break;
                case "416002"://목차
                    if (varOpenPopLayer.find("#dev_tocUpdate").val().trim() == "") {
                        valimsg = "수정하실 목차 내용을 입력해 주세요.";
                    } else {
                        url = "/journal/errReport/toc";
                        param.toc = "1. Introduction
2. Rotor Design of High Speed Motor
3. Optimization Design of Magnetic Flux Density Distribution
4. Experimental Verification
5. Conclusion
References
";
                        param.utoc = varOpenPopLayer.find("#dev_tocUpdate").val().replace(/\n/g, '
');
                    }
                    break;

                case "416003"://페이지
                    if (varOpenPopLayer.find("#dev_update_spage").val().trim() == "" && varOpenPopLayer.find("#dev_update_epage").val() == "") {
                        valimsg = "수정하실 페이지 정보를 입력해 주세요.";
                    } else {
                        url = "/journal/errReport/page";
                        param.start = '430';
                        param.end = '434';
                        param.ustart = varOpenPopLayer.find("#dev_update_spage").val();
                        param.uend = varOpenPopLayer.find("#dev_update_epage").val();
                    }
                    break;

                case "416006"://저자
                    varOpenPopLayer.find('#dev_autrErr input[name=autrNm]').each(function () {
                        if ($(this).val().trim() == "") {
                            valimsg = "수정하실 저자명을 입력해 주세요.";
                            $(this).focus();
                            return;
                        }
                    });

                    url = "/journal/errReport/author";
                    param = varOpenPopLayer.find("#dev_autrErr").serialize();
                    break;
            }

            if (valimsg != "") {
                $.jQueryAlert(valimsg, varLang);
            } else {
                $.fn.closeLayerNew('#openPopLayer');
                ajaxSend(url, param);
                sendMail(param.nodeId);	// 이메일 발송 요청
            }

            fn_statistics('Z401', 'ICST00001411', '');
        }
    });

    //알림서비스 신청 팝업
    function alarmPopup() {
        if (dev_b2cId_chk != "") {
