    //알림 서비스 신청
    function alarmReq() {
        var params = {};
        var checkedPlct, uncheckedPlct;
        var checkedNode, uncheckedNode;
        var checkedAutrs = [];
        var checkedAutr_quotes = [];
        var unCheckedAutrs = [];
        var unCheckedAutr_quotes = [];
        var chk_len = $("#frm_alarm input[type='checkbox']:checked").length;

        $("#frm_alarm input[type='checkbox']").each(function () {
            if ($(this).val() != "on") {
                var key = $(this).attr("name");
                var val = $(this).val();
                if ($(this).prop('checked')) {
                    if (key == "autr") {
                        //autrs.push(val);
                        checkedAutrs.push(val);
                    } else if (key == "autr_quote") {
                        checkedAutr_quotes.push(val);
                    } else if (key === 'plct') {
                        params.checkedPlct = val;
                    } else if (key === 'node_quote') {
                        params.checkedNode = val;
                    }
                } else {
                    if (key == "autr") {
                        //autrs.push(val);
                        unCheckedAutrs.push(val);
                    } else if (key == "autr_quote") {
                        unCheckedAutr_quotes.push(val);
                    } else if (key === 'plct') {
                        params.uncheckedPlct = val;
                    } else if (key === 'node_quote') {
                        params.uncheckedNode = val;
                    }
                }
            }
        });
        params.chAutr = checkedAutrs;
        params.chAutr_quote = checkedAutr_quotes;
        params.unchAutr = unCheckedAutrs;
        params.unchAutr_quote = unCheckedAutr_quotes;

        $.ajax({
            url: ProxyUrl + "/journal/alarmRegist/articleDetail",
            type: "POST",
            data: params,
            dataType: "json",
            success: function (data) {
                if (data.failMsg) {
                    $.jQueryAlert(data.failMsg);
                } else {
                    $.fn.closeLayer("#pub_modalApplyAlarm");
                    $.jQueryAlert("알림이 설정되었습니다.");
                    alarmCheck();
                }
                //21.10.22, UI 변경으로 사용하지 않는 알림 서비스 팝업 삭제, jaekyungkim
            },
            error: function (e) {
                console.log(e);
            },
            complete: function () {
                //loadingClose();
            }
        });

    }

    // //인용양식 팝업
    function fnQuoteModal(node_id) {

        fnSearchUserQuote();
        params.node_id = node_id;
        var nodeId = node_id.split(";");
        $("#dev_node_id").val(node_id);

        $.ajax({
            type: "POST",
            url: ProxyUrl + "/mylib/lastQuoteTitle",
            data: params,
            success: function (data) {
                if (nodeId.length != 1) {

                    $("#newQuote").hide();

                } else {

                    $("#newQuote").show();
                    $("#quoteTtl").text(data.quoteTtl.replace(//g, '').replace(/<\/ITLC>/g, '').replace(/ /g, ''));
                    //인용한 양식 복사하기를 위한 세팅
                    $("#copyText").val(data.quoteTtl.replace(//g, '').replace(/<\/ITLC>/g, '').replace(/ /g, ''));
                    $("#quoteTitle").text(" _ " + data.title);
                    $("#dev_lang").val(data.quote_lang);
                }

            }
        });

        $.fn.openLayer("#pub_modalQuoteThesis");

    }

