    //참고문헌 - 이름 추출
    function getNodeName() {
        var nodeList = [];
        var msg = "";
        $("input[name=modalApplyReferenceStep_url]").each(function () {
            var url = $(this).val();
            if (url != "") {
                var idx = url.indexOf("NODE");
                if (idx < 0) {
                    msg = "DBpia 논문페이지의 URL을 입력해 주세요.";
                    return false;
                } else {
                    var id = url.substring(idx, idx + 12);
                    if (id == $("#articleId").val()) {
                        msg = "보고 계신 논문과 동일한 논문입니다. 다시 입력해 주세요.";
                        return false;
                    } else {
                        nodeList.push(id);
                    }
                }
            }
        });
        if (msg != "") {
            $.jQueryAlert(msg);
            return;
        }

        if (nodeList.length > 0) {
            var uniq = nodeList.reduce(function (a, b) {
                if (a.indexOf(b) < 0) a.push(b);
                return a;
            }, []);

            var param = {};
            param.node = uniq;

            $.ajax({
                url: ProxyUrl + "/journal/getNodeName",
                type: "POST",
                data: param,
                dataType: "json",
                success: function (data) {
                    if (data.list.length <= 0) {
                        $.jQueryAlert("DBpia 논문페이지의 URL을 입력해 주세요.");
                    } else {
                        nodeNamePrint(data.list);
                    }
                },
                error: function (e) {
                    console.log(e);
                    alert("조회결과가 없습니다.");
                }
            });
        } else {
            //여기 얼럿창
            $.jQueryAlert("DBpia 논문페이지의 URL을 입력해 주세요.");
            return false;
        }

    }

    //참고문헌 등록
    function refBookRegist() {
        var param = {};
        param.node = "NODE07239686";
        param.notdNode = [];
        $("#pub_modalApplyReferenceStep2").find("input[name='notdNode']").each(function () {
            param.notdNode.push($(this).val());
        });
        if (param.notdNode.length > 0) {
            ajaxSend("/journal/setRefBook", param);
            fn_statistics('Z315', 'ICST00001411', '');	// 기능이용통계 데이터 생성
        } else {
            $.jQueryAlert("등록할 참고문헌이 없습니다.");
        }
    }


    function ajaxSend(url, param) {
        $.ajax({
            url: ProxyUrl + url,
            type: "POST",
            data: param,
            dataType: "json",
            success: function (data) {
                var msg = "";
                if (data.success < 0) {
                    msg = "로그인해야 이용하실 수 있습니다.";
                } else if (data.success == 0) {
                    msg = "등록이 완료되었습니다.";
                } else {
                    if (data.results == null) {
                        msg = "등록 실패. 다시 시도해 주세요.";
                    }
                }
                //}
                if (msg != "") {
                    $.jQueryAlert(msg);
                }
            },
            error: function (e) {
                console.log(e);
            }
        });
    }

    //참고 문헌 신고 1단계 출력
    function nodeNamePrint(list) {
        var content = "";
        list.forEach(function (item, idx) {
            if ("K" == "E" && item.articleTitleEn != '') {
                content += '
' + item.articleTitleEn;
            } else {
                content += '
' + item.articleTitleKr;
            }
            content += '<input " type="hidden" name="notdNode" value="' + item.articleId + '"/>';
            content += '';
            content += '삭제';
            content += '';
        });

        $("#pub_modalApplyReferenceStep2").find("ul.listArea").empty();
        $("#pub_modalApplyReferenceStep2").find("ul.listArea").append(content);
        $.fn.closeLayer('#pub_modalApplyReferenceStep1');
        $.fn.openLayer('#pub_modalApplyReferenceStep2');
    }

