    //인용하기 클립보드복사
    function fnCopyClipboard() {

        var nodeId = "NODE07239686";
        var copytext = document.getElementById("copyText");

        var content = copytext.value;
        content = content.replace(//g, '').replace(/<\/ITLC>/g, '');
        copytext.value = content;

        copytext.style.display = 'block';
        copytext.select();
        document.execCommand("copy");
        copytext.style.display = 'none';

        //논문 정보 복사 하기 완료 텍스트 팝업 출력
        $.jQueryAlert("논문의 정보가 복사되었습니다.
 논문을 인용할 위치에서 붙여넣기 하세요.");

    }

