    // 특수문자 제거
    function regExpCheck(dest) {
        var regExp = /[\{\}\[\]\/;:|\)*~`^\<>@#$%&\\\=\(\'\"]/gi;
        if (regExp.test(dest)) {
            return dest.replace(regExp, '');
        } else {
            return dest;
        }
    }

    function alarmCheck() {
        if (dev_b2cId_chk != "") {
            var params = {
                b2cId: '',
                autrId: '',
            };
            $.ajax({
                type: 'POST',
                url: '/journal/getAlarmCheck',
                dataType: 'json',
                data: params,
                async: true,
                success: function (data) {
                    var checkedCntOuter = 0, checkedAutrCntInner = 0, checkedCitedCntInner = 0;

                    if (data) {
                        var autrNewInputs = document.querySelectorAll('#dev_autr_list input');
                        var autrCitedInputs = document.querySelectorAll('#dev_cited_list input');
                        data.forEach(function (item) {
                            if (item.ALRT_DIV_CODE === '512001' && item.DEST === 'PLCT00000799') { 		// 간행물 알람 설정
                                $("#ck_model_alarm_01").prop("checked", true);
                                checkedCntOuter++;
                            } else if (item.ALRT_DIV_CODE === '512004' && item.NODE_DIV_CODE === '513002') {		// 관심저자 신규 논문 알림
                                Array.prototype.forEach.call(autrNewInputs, function (el) {
                                    if (el.value === item.DEST) {
                                        el.checked = true;
                                        checkedAutrCntInner++;
                                        if (checkedAutrCntInner === autrCitedInputs.length) {
                                            $('#ck_model_alarm_03').prop('checked', true);
                                            checkedCntOuter++;
                                        }
                                    }
                                });
                            } else if (item.ALRT_DIV_CODE === '512004' && item.NODE_DIV_CODE === '513001') {		//
                                Array.prototype.forEach.call(autrCitedInputs, function (el) {
                                    if (el.value === item.DEST) {
                                        el.checked = true;
                                        checkedCitedCntInner++;

                                        if (checkedCitedCntInner === autrCitedInputs.length) {
                                            $('#ck_model_alarm_04').prop('checked', true);
                                            checkedCntOuter++;
                                        }
                                    }
                                });
                            } else if (item.ALRT_DIV_CODE = '512005' && item.DEST === 'NODE07239686') {
                                $("#ck_model_alarm_05").prop("checked", true);
                                checkedCntOuter++;
                            }
                        });

                        var alarmBell = document.querySelector('#article-alarm #alarm-icon');
                        if (checkedCntOuter === 4) {
                            $("#article-alarm").addClass('active');
                        } else {
                            $("#article-alarm").removeClass('active');
                        }
                    }
                },
                error: function (request, status, error) {
                    alert("code:" + request.status + "\n" + "error:" + error);
                }
            });
        }
    }

    // 인기논문 배너 작동
    function bannerPopup(banName) {
        $.fn.openLayer('#' + banName);
    }

$(window).on('load resize', function () {
     var windowWidth = $(this).width()
     if (windowWidth <= 526) {
         // 모달 이용수-피인용수 그래프 ul-li 너비 가변
         $(".jcarousel ul.list li.item").width(windowWidth);
         $(".jcarousel ul.list").width(windowWidth * '2');
     }
 });
