    function alertconfirmed() {
// Send ajax to confirm
document.getElementById('confirmAlertButton').disabled = 'disabled';
$.ajax({
cache: false,
type: "GET",
url: "set_citation_alert.cfm?uri=oe-26-5-6249&confirmalert=true"
}).done(function( ajxretmsg ) {
if (ajxretmsg == "1") {
document.getElementById('alertConfirmArea').innerHTML = 'Your alert has been set!

';
} else {
document.getElementById('alertConfirmArea').innerHTML = 'There was an error setting your alert. Please try again later.

';
}
});
}

